import { ADD_FAVORITE, DELETE_FOVORITE } from "./action-types";



export const addFavorite = (character) => {
    return { type: ADD_FAVORITE, payload: character }
}

export const deleteFavorite = (id) => {

    return { type: DELETE_FOVORITE, payload: id }

}
