import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { addFavorite, deleteFavorite } from '../actions/favorites';
import style from './Card.module.css';

const Card = ({ id, name, species, gender, image, onClose }) => {
   const [isFav, setIsFav] = useState(false);
   const dispatch = useDispatch();
   const myFavorites = useSelector((state) => state.myFavorites);

   useEffect(() => {
      // Comprobar si el personaje est치 en favoritos
      myFavorites?.forEach((fav) => {
         if (fav.id === id) {
            setIsFav(true);
         }
      });
   }, [myFavorites, id]);

   const handleFavorite = () => {
      // A침adir o eliminar de favoritos seg칰n corresponda
      if (isFav) {
         setIsFav(false);
         dispatch(deleteFavorite(id));
      } else {
         setIsFav(true);
         dispatch(addFavorite({ id, name, species, gender, image }));
      }
   };

   return (
      <div className={style.container}>
         {/* Bot칩n de favorito */}
         <button onClick={handleFavorite} className={style.favoriteButton}>
            {isFav ? '仇벒잺' : '游밼'}
         </button>
         {/* Enlace al detalle del personaje */}
         <Link to={`/detail/${id}`}>
            <h2>Name: {name}</h2>
         </Link>
         {/* Bot칩n de cierre */}
         <button onClick={onClose} className={style.closeButton}>
            X
         </button>
         {/* Informaci칩n del personaje */}
         <h2>Species: {species}</h2>
         <h2>Gender: {gender}</h2>
         {/* Imagen del personaje */}
         <img src={image} alt={name} className={style.image} />
      </div>
   );
};

export default Card;
